package JavaProgram;

public class Person {
	
	void onStart() {
		System.out.println("Light is on");
	}
	void onOff() {
		System.out.println("Light is off");
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Person p =new Person();
		p.onStart();
		p.onOff();
		

	}

}
